    font-weight: 500;
}

.logout-button {
    background: #e53e3e;
    color: white;
    border: none;
    padding: 8px 16px;
    border-radius: 6px;
    font-size: 14px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s ease;
}

.logout-button:hover {
    background: #c53030;
    transform: translateY(-1px);
}

.dashboard-main {
    flex: 1;
    display: grid;
    grid-template-columns: 2fr 1fr;
    gap: 24px;
