    align-items: center;
    justify-content: center;
    line-height: 1.6;
}

/* Login Page Styles */
.container {
    width: 100%;
    max-width: 450px;
    margin: 0 auto;
    padding: 20px;
}

.login-container {
    background: rgba(255, 255, 255, 0.98);
