}

.logo-section {
    text-align: center;
    margin-bottom: 40px;
    position: relative;
}

.logo {
    display: inline-block;
    margin-bottom: 15px;
}

.brand-name {
    font-size: 32px;
    font-weight: 700;
    color: #1e3c72;
    margin-bottom: 5px;
    display: inline-block;
}

.version {
    position: absolute;
    top: 0;
    right: calc(50% - 100px);
    font-size: 14px;
    color: #666;
    font-weight: 500;
}

.form-section h2 {
    font-size: 24px;
    font-weight: 600;
    color: #2d3748;
    margin-bottom: 30px;
    text-align: center;
}

.form-group {
    margin-bottom: 24px;
}

.form-group label {
    display: flex;
    align-items: center;
    font-size: 14px;
    font-weight: 500;
    color: #4a5568;
    margin-bottom: 8px;
}

.form-group label .icon {
    margin-right: 8px;
    color: #718096;
}

.form-group input[type="email"],
.form-group input[type="password"] {
    width: 100%;
    padding: 12px 16px;
    border: 2px solid #e2e8f0;
    border-radius: 8px;
    font-size: 16px;
    transition: all 0.2s ease;
    background-color: #f7fafc;
}

.form-group input:focus {
    outline: none;
    border-color: #00A2E8;
    background-color: white;
    box-shadow: 0 0 0 3px rgba(0, 162, 232, 0.1);
}

.password-input-container {
    position: relative;
}

.toggle-password {
    position: absolute;
    right: 12px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    color: #718096;
    padding: 4px;
    border-radius: 4px;
    transition: color 0.2s ease;
}

.toggle-password:hover {
    color: #4a5568;
}

.radio-group {
    display: flex;
    gap: 20px;
    margin: 25px 0;
}

.radio-option {
    display: flex;
    align-items: center;
    gap: 8px;
}

.radio-option input[type="radio"] {
    width: 18px;
    height: 18px;
    accent-color: #00A2E8;
}

.radio-option label {
    font-size: 14px;
    color: #4a5568;
    cursor: pointer;
    margin: 0;
}

.login-button {
    width: 100%;
    background: linear-gradient(135deg, #00A2E8 0%, #0074B7 100%);
    color: white;
    border: none;
    padding: 14px 24px;
    border-radius: 8px;
    font-size: 16px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s ease;
    position: relative;
    min-height: 48px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.login-button:hover {
    transform: translateY(-1px);
    box-shadow: 0 8px 20px rgba(0, 162, 232, 0.3);
}

.login-button:active {
    transform: translateY(0);
}

.login-button:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
}

.spinner {
    width: 20px;
    height: 20px;
    border: 2px solid rgba(255, 255, 255, 0.3);
    border-top: 2px solid white;
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin-left: 10px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.error-message {
    background-color: #fed7d7;
    border: 1px solid #feb2b2;
    color: #c53030;
    padding: 12px 16px;
    border-radius: 8px;
    font-size: 14px;
