}

.credential:last-child {
    margin-bottom: 0;
}

/* Dashboard Styles */
.dashboard-body {
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    min-height: 100vh;
    display: block;
    align-items: stretch;
    justify-content: stretch;
}

.dashboard-container {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

.dashboard-header {
    background: white;
