    color: #2d3748;
}

.credential:last-child {
    margin-bottom: 0;
}

/* Dashboard Styles */
.dashboard-body {
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    min-height: 100vh;
    display: block;
    align-items: stretch;
    justify-content: stretch;
}

.dashboard-container {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

.dashboard-header {
    background: white;
    padding: 16px 24px;
    border-bottom: 1px solid #e2e8f0;
    display: flex;
    justify-content: space-between;
    align-items: center;
