    if (err) {
      console.error('Database error:', err);
      return res.status(500).json({ error: 'Failed to retrieve users' });
    }

    res.json({ users });
  });
});

// Logout endpoint
app.post('/api/logout', (req, res) => {
  res.clearCookie('auth_token');
  res.json({ success: true, message: 'Logged out successfully' });
