    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    padding: 5px;
}

.show-password svg {
    width: 20px;
    height: 20px;
    fill: #999;
}

/* Computer type radio buttons */
.computer-type {
    margin: 25px 0;
    display: flex;
    justify-content: center;
    gap: 30px;
}

.radio-label {
    display: flex;
    align-items: center;
    cursor: pointer;
    font-size: 14px;
    color: #555;
}

.radio-label input[type="radio"] {
    display: none;
}

.checkmark {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    margin-right: 8px;
    position: relative;
    border: 2px solid #ddd;
}

.checkmark.private {
    background-color: white;
}

.checkmark.public {
    background-color: white;
}

.radio-label input[type="radio"]:checked ~ .checkmark.private {
    border-color: #00bcd4;
}

.radio-label input[type="radio"]:checked ~ .checkmark.private::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background-color: #00bcd4;
}

.radio-label input[type="radio"]:checked ~ .checkmark.public {
    border-color: #ff5252;
}

.radio-label input[type="radio"]:checked ~ .checkmark.public::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
