    background: linear-gradient(135deg, #1a237e 0%, #0d47a1 100%);
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Container */
.container {
    width: 100%;
    max-width: 900px;
    padding: 20px;
}

/* Logo section */
.logo-section {
    text-align: center;
    margin-bottom: 30px;
}

.logo {
    width: 280px;
    height: 80px;
    margin-bottom: 10px;
}

.version {
    color: #ffffff;
    font-size: 18px;
    opacity: 0.8;
}

/* Login box */
.login-box {
    background: white;
    border-radius: 8px;
    padding: 40px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
    max-width: 450px;
    margin: 0 auto;
}

.login-box h2 {
    text-align: center;
    margin-bottom: 30px;
    color: #333;
    font-size: 24px;
    font-weight: 600;
}

/* Error message */
.error-message {
    background-color: #fee;
    color: #c33;
    padding: 10px 15px;
    border-radius: 4px;
    margin-bottom: 20px;
    text-align: center;
    font-size: 14px;
}

.success-message {
    background-color: #efe;
    color: #3c3;
    padding: 10px 15px;
    border-radius: 4px;
    margin-bottom: 20px;
    text-align: center;
    font-size: 14px;
}

/* Form groups */
.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: flex;
    align-items: center;
    margin-bottom: 8px;
    color: #555;
    font-size: 14px;
}

.icon {
    width: 20px;
    height: 20px;
    margin-right: 8px;
    fill: #666;
}

.form-group input {
    width: 100%;
    padding: 12px 15px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 16px;
    transition: border-color 0.3s;
}

.form-group input:focus {
    outline: none;
    border-color: #00bcd4;
}

/* Password wrapper */
.password-wrapper {
    position: relative;
}

.show-password {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    padding: 5px;
}

.show-password svg {
    width: 20px;
    height: 20px;
    fill: #999;
}

/* Computer type radio buttons */
.computer-type {
    margin: 25px 0;
    display: flex;
    justify-content: center;
    gap: 30px;
}

.radio-label {
    display: flex;
    align-items: center;
    cursor: pointer;
    font-size: 14px;
    color: #555;
}

.radio-label input[type="radio"] {
    display: none;
}

.checkmark {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    margin-right: 8px;
    position: relative;
    border: 2px solid #ddd;
}

.checkmark.private {
    background-color: white;
}

.checkmark.public {
    background-color: white;
}

.radio-label input[type="radio"]:checked ~ .checkmark.private {
    border-color: #00bcd4;
}

.radio-label input[type="radio"]:checked ~ .checkmark.private::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background-color: #00bcd4;
}

.radio-label input[type="radio"]:checked ~ .checkmark.public {
    border-color: #ff5252;
}

.radio-label input[type="radio"]:checked ~ .checkmark.public::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background-color: #ff5252;
}

/* Connect button */
.connect-btn {
    width: 200px;
    padding: 14px;
    background-color: #00bcd4;
    color: white;
    border: none;
    border-radius: 4px;
    font-size: 16px;
    font-weight: 600;
    cursor: pointer;
    display: block;
    margin: 30px auto 0;
    transition: background-color 0.3s;
}

.connect-btn:hover {
    background-color: #00acc1;
}

.connect-btn:active {
