    color: #333;
}

/* Form styles */
.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.9rem;
    color: #555;
    margin-bottom: 8px;
    font-weight: 500;
}

.icon {
    width: 18px;
    height: 18px;
}

input[type="email"],
input[type="password"],
input[type="text"] {
    width: 100%;
    padding: 12px 15px;
    border: 1px solid #ddd;
    border-radius: 6px;
    font-size: 1rem;
    transition: border-color 0.3s, box-shadow 0.3s;
}

input[type="email"]:focus,
input[type="password"]:focus,
input[type="text"]:focus {
    outline: none;
    border-color: #00CED1;
    box-shadow: 0 0 0 2px rgba(0, 206, 209, 0.1);
}

/* Password input with toggle */
.password-input {
    position: relative;
}

.password-toggle {
    position: absolute;
    right: 12px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    padding: 5px;
    color: #666;
    transition: color 0.3s;
}

.password-toggle:hover {
    color: #00CED1;
}

/* Radio button styles */
.remember-options {
    display: flex;
    gap: 30px;
    margin: 25px 0;
    justify-content: center;
}

.radio-option {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    font-size: 0.9rem;
    color: #555;
}

.radio-option input[type="radio"] {
    display: none;
}

.checkmark {
    width: 18px;
    height: 18px;
    border: 2px solid #ddd;
    border-radius: 50%;
    position: relative;
    transition: all 0.3s;
}

.checkmark.private {
    border-color: #00CED1;
}

.checkmark.public {
    border-color: #ff6b6b;
}

.radio-option input:checked ~ .checkmark::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: currentColor;
}

.radio-option input:checked ~ .checkmark.private {
    background: rgba(0, 206, 209, 0.1);
    border-color: #00CED1;
}

.radio-option input:checked ~ .checkmark.private::after {
    background: #00CED1;
}

.radio-option input:checked ~ .checkmark.public {
    background: rgba(255, 107, 107, 0.1);
    border-color: #ff6b6b;
}

.radio-option input:checked ~ .checkmark.public::after {
    background: #ff6b6b;
}

/* Connect button */
.connect-btn {
    width: 100%;
    padding: 14px;
    background: #00CED1;
    color: white;
    border: none;
    border-radius: 6px;
