}

.connect-btn:hover {
    background: #00B8C5;
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(0, 206, 209, 0.3);
}

.connect-btn:active {
    transform: translateY(0);
}

.connect-btn:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
}

/* Messages */
.error-message,
.info-message {
    margin-top: 15px;
    padding: 12px;
    border-radius: 6px;
    font-size: 0.9rem;
    display: none;
}

.error-message {
    background: #fee;
    color: #d00;
    border: 1px solid #fcc;
}

.info-message {
    background: #e6f7ff;
    color: #0066cc;
    border: 1px solid #b3d9ff;
}

/* Demo info */
.demo-info {
    margin-top: 30px;
    padding-top: 25px;
    border-top: 1px solid #e0e0e0;
}

.demo-info h3 {
    font-size: 0.9rem;
    color: #666;
    margin-bottom: 10px;
}

.demo-info ul {
    list-style: none;
}

.demo-info li {
    font-size: 0.85rem;
    color: #888;
    padding: 3px 0;
    font-family: 'Courier New', monospace;
}

/* Footer */
footer {
    text-align: center;
    margin-top: 30px;
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.85rem;
}

footer p {
    margin: 5px 0;
}

/* Dashboard styles */
.dashboard-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
    background: #f5f7fa;
    min-height: 100vh;
}

.dashboard-header {
    background: white;
    padding: 20px;
    border-radius: 8px;
    margin-bottom: 20px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.user-info {
    display: flex;
    align-items: center;
    gap: 15px;
}

.user-info .avatar {
    width: 40px;
    height: 40px;
    background: #00CED1;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-weight: bold;
}

.logout-btn {
    background: #ff6b6b;
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 6px;
    cursor: pointer;
    transition: background 0.3s;
}

.logout-btn:hover {
    background: #ff5252;
}

/* Message interface */
.message-container {
    display: grid;
    grid-template-columns: 250px 1fr;
    gap: 20px;
    margin-top: 20px;
}

.sidebar {
    background: white;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.sidebar button {
    width: 100%;
    padding: 12px;
    margin-bottom: 10px;
    border: none;
    border-radius: 6px;
    background: #f0f0f0;
    cursor: pointer;
    transition: all 0.3s;
}

.sidebar button.active,
.sidebar button:hover {
    background: #00CED1;
    color: white;
}

.message-list {
    background: white;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.message-item {
    padding: 15px;
    border-bottom: 1px solid #eee;
    cursor: pointer;
    transition: background 0.3s;
}

.message-item:hover {
    background: #f8f9fa;
}

.message-item.unread {
    background: #e6f7ff;
    font-weight: 600;
}

.message-item h3 {
    margin-bottom: 5px;
    color: #333;
}

.message-item .meta {
    font-size: 0.85rem;
    color: #666;
}

/* Compose modal */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: none;
    justify-content: center;
    align-items: center;
