                const userId = this.lastID;
                db.run(`INSERT INTO messages (sender_id, recipient_id, subject, message) VALUES (?, ?, ?, ?)`, 
                    [userId, userId, 'Welcome!', 'This is your first message.']);
            }
        });
    });
});

