{"content":"const sqlite3 = require('sqlite3').verbose();
const bcrypt = require('bcrypt');

const DBSOURCE = "db.sqlite";

let db = new sqlite3.Database(DBSOURCE, (err) => {
    if (err) {
      // Cannot open database
      console.error(err.message)
      throw err
    }else{
        console.log('Connected to the SQLite database.');
        db.serialize(() => {
            db.run(`CREATE TABLE IF NOT EXISTS users (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                email TEXT UNIQUE,
                password TEXT
            )`, (err) => {
                if (err) {
                    // Table already created
                } else {
                    // Table just created, creating some rows
                    const saltRounds = 10;
                    bcrypt.hash('password123', saltRounds, (err, hash) => {
                        db.run(`INSERT INTO users (email, password) VALUES (?, ?)`, ['user@example.com', hash]);
                    });
                }
            });

            db.run(`CREATE TABLE IF NOT EXISTS messages (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                user_id INTEGER,
                subject TEXT,
                message TEXT,
                FOREIGN KEY (user_id) REFERENCES users (id)
            )`, (err) => {
                if (err) {
                    // Table already created
                } else {
                    // Table just created, creating some rows
                    db.get('SELECT id FROM users WHERE email = ?', ['user@example.com'], (err, user) => {
                        if (user) {
                            db.run(`INSERT INTO messages (user_id, subject, message) VALUES (?, ?, ?)`, 
                                [user.id, 'Welcome!', 'This is your first message.']);
                        }
                    });
                }
            });
        });
    }
});

module.exports = db;
