    db.all("SELECT * FROM messages WHERE receiver_email = ? ORDER BY timestamp DESC", [userEmail], (err, rows) => {
        if (err) {
            return res.send("Error retrieving messages");
        }
        res.render('dashboard', { user: req.session.user, messages: rows });
    });
});

