        WHERE m.receiver_email = ? OR m.sender_id = ?
        ORDER BY m.timestamp DESC
    `;

    db.all(sql, [userEmail, userId], (err, messages) => {
        if (err) {
            console.error(err);
            return res.status(500).send("Database error");
        }
        res.render('dashboard', { user: req.session, messages: messages });
    });
});

