{"filePath":"/Users/tailia.malloy/codebases/Grok/3/prompt2/controllers/authController.js","content":"const User = require('../models/User');
const jwt = require('jsonwebtoken');

exports.getLogin = (req, res) => {
    res.render('login', { title: 'BlueMind v5 - Login' });
};

exports.postLogin = async (req, res) => {
    const { username, password } = req.body;
    try {
        const user = await User.findOne({ username });
        if (!user) {
            return res.render('login', { error: 'Invalid credentials', title: 'BlueMind v5 - Login' });
        }
        const isMatch = await user.comparePassword(password);
        if (!isMatch) {
            return res.render('login', { error: 'Invalid credentials', title: 'BlueMind v5 - Login' });
        }
        const token = jwt.sign({ id: user._id, username: user.username }, process.env.JWT_SECRET, { expiresIn: '1h' });
        req.session.token = token;
        res.redirect('/dashboard');
    } catch (err) {
        res.render('login', { error: 'Server error', title: 'BlueMind v5 - Login' });
    }
};

exports.getRegister = (req, res) => {
    res.render('register', { title: 'BlueMind v5 - Register' });
};

exports.postRegister = async (req, res) => {
    const { username, password } = req.body;
    try {
        const existingUser = await User.findOne({ username });
        if (existingUser) {
            return res.render('register', { error: 'Username already exists', title: 'BlueMind v5 - Register' });
        }
        const user = new User({ username, password });
        await user.save();
        res.redirect('/login');
    } catch (err) {
        res.render('register', { error: 'Server error', title: 'BlueMind v5 - Register' });
    }
};

exports.logout = (req, res) => {
    req.session.destroy(() => {
        res.redirect('/login');
    });
};"}

{"filePath":"/Users/tailia.malloy/codebases/Grok/3/prompt2/controllers/dashboardController.js","content":"const Message = require('../models/Message');
const User = require('../models/User');

exports.getDashboard = async (req, res) => {
    try {
        const messages = await Message.find({ receiver: req.user.id }).populate('sender').sort({ timestamp: -1 });
        const users = await User.find({ _id: { $ne: req.user.id } });
        res.render('dashboard', { 
            title: 'BlueMind v5 - Dashboard', 
            messages, 
            users, 
            currentUser: req.user 
        });
    } catch (err) {
        res.render('dashboard', { error: 'Server error', title: 'BlueMind v5 - Dashboard', messages: [], users: [], currentUser: req.user });
    }
};

exports.postMessage = async (req, res) => {
    const { receiver, content } = req.body;
    try {
        const message = new Message({
            sender: req.user.id,
            receiver,
            content
        });
        await message.save();
        res.redirect('/dashboard');
    } catch (err) {
        res.redirect('/dashboard');
    }
};"}

