{"filePath":"/Users/tailia.malloy/codebases/Grok/3/prompt5/BlueMind-v5/routes/authRoutes.js","content":"const express = require('express');
const router = express.Router();
const passport = require('passport');
const LocalStrategy = require('passport-local').Strategy;
const User = require('../models/User');

// Passport configuration
passport.use(new LocalStrategy(
  async (username, password, done) => {
    try {
      const user = await User.findOne({ username: username });
      if (!user) {
        return done(null, false, { message: 'Incorrect username.' });
      }
      const isMatch = await user.comparePassword(password);
      if (!isMatch) {
        return done(null, false, { message: 'Incorrect password.' });
      }
      return done(null, user);
    } catch (err) {
      return done(err);
    }
  }
));

passport.serializeUser((user, done) => {
  done(null, user.id);
});

passport.deserializeUser(async (id, done) => {
  try {
    const user = await User.findById(id);
    done(null, user);
  } catch (err) {
    done(err, null);
  }
});

// Routes
router.get('/', (req, res) => {
  res.render('login', { message: req.flash('error') });
});

router.get('/register', (req, res) => {
  res.render('register');
});

router.post('/register', async (req, res) => {
  try {
    const { username, password } = req.body;
    const newUser = new User({ username, password });
    await newUser.save();
    res.redirect('/');
  } catch (err) {
    res.render('register', { error: 'Username already exists or other error occurred.' });
  }
});

router.post('/login', passport.authenticate('local', {
  successRedirect: '/messages',
  failureRedirect: '/',
  failureFlash: true
}));

router.get('/logout', (req, res) => {
  req.logout();
  res.redirect('/');
});

module.exports = router;"}

{"filePath":"/Users/tailia.malloy/codebases/Grok/3/prompt5/BlueMind-v5/routes/messageRoutes.js","content":"const express = require('express');
const router = express.Router();
const Message = require('../models/Message');
const User = require('../models/User');

// Middleware to ensure user is authenticated
function isAuthenticated(req, res, next) {
  if (req.isAuthenticated()) {
    return next();
  }
  res.redirect('/');
}

// Display messages
router.get('/', isAuthenticated, async (req, res) => {
  try {
    const messages = await Message.find({ 
      $or: [{ sender: req.user._id }, { recipient: req.user._id }]
    })
    .populate('sender')
    .populate('recipient');
    const users = await User.find({ _id: { $ne: req.user._id } });
    res.render('messages', { messages, users, currentUser: req.user });
  } catch (err) {
    console.error(err);
    res.redirect('/');
  }
});

// Send message
router.post('/send', isAuthenticated, async (req, res) => {
  try {
    const { recipient, content } = req.body;
    const newMessage = new Message({
      sender: req.user._id,
      recipient,
      content
    });
    await newMessage.save();
    res.redirect('/messages');
  } catch (err) {
    console.error(err);
    res.redirect('/messages');
  }
});

module.exports = router;"}

