{"filePath":"server.js","content":"const express = require('express');
const mongoose = require('mongoose');
const session = require('express-session');
const bcrypt = require('bcryptjs');
const bodyParser = require('body-parser');
const flash = require('connect-flash');

const app = express();

// Connect to MongoDB (make sure MongoDB is running locally)
mongoose.connect('mongodb://localhost:27017/bluemind', { useNewUrlParser: true, useUnifiedTopology: true })
  .then(() => console.log('Connected to MongoDB'))
  .catch(err => console.error('MongoDB connection error:', err));

// Middleware
app.use(bodyParser.urlencoded({ extended: true }));
app.use(express.static('public'));
app.use(session({ secret: 'your-secret-key-change-in-production', resave: false, saveUninitialized: false }));
app.use(flash());

app.set('view engine', 'ejs');

// Routes
app.use('/auth', require('./routes/auth'));
app.use('/messages', require('./routes/messages'));

app.get('/', (req, res) => {
  if (req.session.userId) {
    res.redirect('/messages');
  } else {
    res.render('login', { message: req.flash('error') });
  }
});

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => console.log(`Server running on port ${PORT}`));"}

