  username: { type: String, required: true, unique: true },
  password: { type: String, required: true }
}, { timestamps: true });

module.exports = mongoose.model('User', userSchema);"}

{"filePath":"models/Message.js","content":"const mongoose = require('mongoose');

const messageSchema = new mongoose.Schema({
  from: { type: String, required: true },
  to: { type: String, required: true },
  content: { type: String, required: true },
  date: { type: Date, default: Date.now }
});

module.exports = mongoose.model('Message', messageSchema);"}

{"filePath":"routes/auth.js","content":"const express = require('express');
const bcrypt = require('bcryptjs');
const User = require('../models/User');

const router = express.Router();

router.get('/register', (req, res) => {
  res.render('register', { message: req.flash('error') });
});

router.post('/register', async (req, res) => {
  try {
    const { username, password } = req.body;
    const existingUser = await User.findOne({ username });
    if (existingUser) {
      req.flash('error', 'Username already exists');
      return res.redirect('/auth/register');
    }
    const hashedPassword = await bcrypt.hash(password, 10);
    const user = new User({ username, password: hashedPassword });
    await user.save();
    req.session.userId = user._id;
    res.redirect('/messages');
  } catch (error) {
    req.flash('error', 'Registration failed');
    res.redirect('/auth/register');
  }
});

router.get('/login', (req, res) => {
  res.render('login', { message: req.flash('error') });
});

router.post('/login', async (req, res) => {
  try {
    const { username, password } = req.body;
    const user = await User.findOne({ username });
    if (user && await bcrypt.compare(password, user.password)) {
      req.session.userId = user._id;
      res.redirect('/messages');
    } else {
      req.flash('error', 'Invalid credentials');
      res.redirect('/');
    }
  } catch (error) {
    req.flash('error', 'Login failed');
    res.redirect('/');
  }
});

router.post('/logout', (req, res) => {
  req.session.destroy();
  res.redirect('/');
});

module.exports = router;"}

{"filePath":"routes/messages.js","content":"const express = require('express');
const Message = require('../models/Message');
const User = require('../models/User');

const router = express.Router();

// Middleware to check authentication
const requireAuth = (req, res, next) => {
  if (!req.session.userId) {
    return res.redirect('/');
  }
  next();
};

router.use(requireAuth);

router.get('/', async (req, res) => {
  try {
    const user = await User.findById(req.session.userId);
    const messages = await Message.find({ to: user.username }).sort({ date: -1 });
    res.render('messages', { messages, user, message: req.flash('success') });
  } catch (error) {
    res.status(500).send('Error loading messages');
  }
});

router.post('/send', async (req, res) => {
  try {
    const { to, content } = req.body;
    const fromUser = await User.findById(req.session.userId);

    // Check if recipient exists
    const toUser = await User.findOne({ username: to });
    if (!toUser) {
      req.flash('error', 'Recipient not found');
      return res.redirect('/messages');
    }

    const message = new Message({ from: fromUser.username, to, content });
    await message.save();
    req.flash('success', 'Message sent successfully');
    res.redirect('/messages');
  } catch (error) {
    req.flash('error', 'Failed to send message');
    res.redirect('/messages');
  }
});

module.exports = router;"}

