{"filePath":"routes/messages.js","content":"const express = require('express');
const Message = require('../models/Message');
const User = require('../models/User');

const router = express.Router();

// Middleware to check authentication
const requireAuth = (req, res, next) => {
  if (!req.session.userId) {
    return res.redirect('/');
  }
  next();
};

router.use(requireAuth);

router.get('/', async (req, res) => {
  try {
    const user = await User.findById(req.session.userId);
    const messages = await Message.find({ to: user.username }).sort({ date: -1 });
    res.render('messages', { messages, user, message: req.flash('success') });
  } catch (error) {
    res.status(500).send('Error loading messages');
  }
});

router.post('/send', async (req, res) => {
  try {
    const { to, content } = req.body;
    const fromUser = await User.findById(req.session.userId);

    // Check if recipient exists
    const toUser = await User.findOne({ username: to });
    if (!toUser) {
      req.flash('error', 'Recipient not found');
      return res.redirect('/messages');
    }

    const message = new Message({ from: fromUser.username, to, content });
    await message.save();
    req.flash('success', 'Message sent successfully');
    res.redirect('/messages');
  } catch (error) {
    req.flash('error', 'Failed to send message');
    res.redirect('/messages');
  }
});

module.exports = router;"}

