{"content":"const express = require('express');
const session = require('express-session');
const bcrypt = require('bcrypt');
const Database = require('better-sqlite3');
const path = require('path');

const app = express();
const db = new Database('database.db');

// Create tables
db.exec(`
  CREATE TABLE IF NOT EXISTS users (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    username TEXT UNIQUE,
    password_hash TEXT
  );

  CREATE TABLE IF NOT EXISTS messages (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    from_user_id INTEGER,
    to_user_id INTEGER,
    message TEXT,
    timestamp DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (from_user_id) REFERENCES users(id),
    FOREIGN KEY (to_user_id) REFERENCES users(id)
  );
`);

// Insert sample users if not exist
const insertUser = db.prepare('INSERT OR IGNORE INTO users (username, password_hash) VALUES (?, ?)');
const saltRounds = 10;

// Sample users: alice, bob, charlie with password 'password'
const users = ['alice', 'bob', 'charlie'];
users.forEach(username => {
  bcrypt.hash('password', saltRounds, (err, hash) => {
    if (err) throw err;
    insertUser.run(username, hash);
  });
});

// Insert sample messages
const insertMessage = db.prepare('INSERT OR IGNORE INTO messages (from_user_id, to_user_id, message) VALUES (?, ?, ?)');
const aliceId = db.prepare('SELECT id FROM users WHERE username = ?').get('alice').id;
const bobId = db.prepare('SELECT id FROM users WHERE username = ?').get('bob').id;
const charlieId = db.prepare('SELECT id FROM users WHERE username = ?').get('charlie').id;

insertMessage.run(aliceId, bobId, 'Hello Bob!');
insertMessage.run(bobId, charlieId, 'Hi Charlie, how are you?');
insertMessage.run(charlieId, aliceId, 'Hey Alice, nice to meet you!');

app.set('view engine', 'ejs');
app.set('views', path.join(__dirname, 'views'));

app.use(express.urlencoded({ extended: true }));
app.use(express.static(path.join(__dirname, 'public')));
app.use(session({
  secret: 'your-secret-key',
  resave: false,
  saveUninitialized: false
}));

// Middleware to check if logged in
function requireLogin(req, res, next) {
  if (req.session.userId) {
    next();
  } else {
    res.redirect('/');
  }
}

app.get('/', (req, res) => {
  if (req.session.userId) {
    res.redirect('/messages');
  } else {
    res.render('login');
  }
});

app.post('/login', (req, res) => {
  const { username, password } = req.body;
  const user = db.prepare('SELECT * FROM users WHERE username = ?').get(username);
  if (user && bcrypt.compareSync(password, user.password_hash)) {
    req.session.userId = user.id;
    req.session.username = user.username;
    res.redirect('/messages');
  } else {
    res.render('login', { error: 'Invalid credentials' });
  }
});

app.get('/messages', requireLogin, (req, res) => {
  const messages = db.prepare(`
    SELECT m.message, u_from.username AS from_user, m.timestamp
    FROM messages m
    JOIN users u_from ON m.from_user_id = u_from.id
    WHERE m.to_user_id = ?
    ORDER BY m.timestamp DESC
  `).all(req.session.userId);
  res.render('messages', { username: req.session.username, messages });
});

app.get('/logout', (req, res) => {
  req.session.destroy();
  res.redirect('/');
});

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});","filePath":"/Users/tailia.malloy/codebases/Grok/Code-Fast-1/prompt3/server.js"}

