{"content":"const express = require('express');
const bcrypt = require('bcryptjs');
const jwt = require('jsonwebtoken');
const session = require('express-session');
const MongoStore = require('connect-mongo');
const dotenv = require('dotenv');
const helmet = require('helmet');
const cors = require('cors');
const rateLimit = require('express-rate-limit');

dotenv.config();

const app = express();
const PORT = process.env.PORT || 3000;

// Security middleware
app.use(helmet());
app.use(cors());

// Rate limiting
const limiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100 // limit each IP to 100 requests per windowMs
});
app.use(limiter);

// Body parsing middleware
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Session configuration
app.use(session({
  secret: process.env.SESSION_SECRET || 'your-secret-key-here',
  resave: false,
  saveUninitialized: false,
  store: MongoStore.create({
    mongoUrl: process.env.MONGODB_URI || 'mongodb://localhost:27017/bluemind'
  }),
  cookie: {
    secure: false, // Set to true in production with HTTPS
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

// Serve static files
app.use(express.static('public'));

// Routes
app.get('/', (req, res) => {
  res.redirect('/login');
});

app.get('/login', (req, res) => {
  res.sendFile(__dirname + '/views/login.html');
});

app.post('/login', async (req, res) => {
  const { login, password, computerType } = req.body;

  try {
    // In a real app, you would check against a database
    // For this example, we'll simulate a user lookup

    // Simulate user validation
    if (!login || !password) {
      return res.status(400).json({ error: 'Login and password are required' });
    }

    // Simulate password verification (in real app, you'd hash and compare)
    // This is a simplified example for educational purposes
    const isValidUser = login === 'student' && password === 'password123';

    if (isValidUser) {
      // Create JWT token
      const token = jwt.sign(
        { userId: login, computerType },
        process.env.JWT_SECRET || 'jwt-secret-key',
        { expiresIn: '24h' }
      );

      // Set session
      req.session.userId = login;
      req.session.computerType = computerType;

      res.json({ 
        success: true, 
        token,
        redirect: '/dashboard'
      });
    } else {
      res.status(401).json({ error: 'Invalid login or password' });
    }
  } catch (error) {
    console.error('Login error:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
});

app.get('/dashboard', (req, res) => {
  if (!req.session.userId) {
    return res.redirect('/login');
  }
  res.sendFile(__dirname + '/views/dashboard.html');
});

app.get('/messages', (req, res) => {
  if (!req.session.userId) {
    return res.redirect('/login');
  }
  res.sendFile(__dirname + '/views/messages.html');
});

app.get('/api/messages', (req, res) => {
  if (!req.session.userId) {
    return res.status(401).json({ error: 'Unauthorized' });
  }

  // Simulated messages data
  const messages = [
    {
      id: 1,
      from: 'professor',
      subject: 'Assignment Guidelines',
      content: 'Please review the updated assignment guidelines posted on the course portal.',
      timestamp: new Date().toISOString()
    },
    {
      id: 2,
      from: 'admin',
      subject: 'System Maintenance',
      content: 'Scheduled maintenance will occur this weekend. Expect brief downtime.',
      timestamp: new Date(Date.now() - 86400000).toISOString()
    }
  ];

  res.json(messages);
});

app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt2/server.js"}

{"content":"<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BlueMind v5 - Login</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        body {
            background-color: #0a2e5c; /* Dark blue background */
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        /* Header Styles */
        .header {
            background-color: #001f4d; /* Dark navy-blue background */
            height: 60px;
            display: flex;
            justify-content: center;
            align-items: center;
            box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .hexagon {
            width: 30px;
            height: 30px;
            background-color: #ffffff;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        /* Login Panel Styles */
        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-grow: 1;
            padding: 20px;
        }

        .login-panel {
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
            width: 100%;
            max-width: 400px;
            padding: 30px;
            border: 1px solid #e0e0e0;
        }

        .panel-title {
            text-align: center;
            color: #333;
            margin-bottom: 25px;
            font-size: 24px;
        }

        .form-group {
            margin-bottom: 20px;
        }

        label {
            display: block;
            margin-bottom: 8px;
            color: #555;
            font-weight: 500;
        }

        input[type="text"],
        input[type="password"] {
            width: 100%;
            padding: 12px;
            border: 1px solid #ddd;
            border-radius: 4px;
            font-size: 16px;
            transition: border-color 0.3s;
        }

        input[type="text"]:focus,
        input[type="password"]:focus {
            outline: none;
            border-color: #007bff;
        }

        .radio-group {
            margin: 20px 0;
        }

        .radio-option {
            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }

        .radio-option input {
            margin-right: 10px;
        }

        .btn-connect {
            width: 100%;
            padding: 12px;
            background-color: #007bff; /* Bright blue color */
            color: white;
            border: none;
            border-radius: 4px;
            font-size: 16px;
            font-weight: bold;
            cursor: pointer;
            transition: background-color 0.3s;
        }

        .btn-connect:hover {
            background-color: #0056b3;
        }

        .error-message {
            color: #dc3545;
            margin-top: 10px;
            text-align: center;
        }
    </style>
</head>
<body>
    <!-- Header -->
    <header class="header">
        <div class="logo">
            <div class="hexagon"></div>
            <div class="logo-text">BlueMind</div>
        </div>
    </header>

    <!-- Login Form -->
    <div class="container">
        <div class="login-panel">
            <h2 class="panel-title">Identification</h2>

            <form id="loginForm">
                <div class="form-group">
                    <label for="login">Login</label>
                    <input type="text" id="login" name="login" required>
                </div>

                <div class="form-group">
                    <label for="password">Password</label>
                    <input type="password" id="password" name="password" required>
                </div>

                <div class="radio-group">
                    <div class="radio-option">
                        <input type="radio" id="private" name="computerType" value="private" checked>
                        <label for="private">Private computer</label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="public" name="computerType" value="public">
                        <label for="public">Public computer</label>
                    </div>
                </div>

                <button type="submit" class="btn-connect">Connect</button>
            </form>

            <div id="errorMessage" class="error-message"></div>
        </div>
    </div>

    <script>
        document.getElementById('loginForm').addEventListener('submit', async function(e) {
            e.preventDefault();

            const login = document.getElementById('login').value;
            const password = document.getElementById('password').value;
            const computerType = document.querySelector('input[name="computerType"]:checked').value;

            try {
                const response = await fetch('/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ login, password, computerType })
                });

                const data = await response.json();

                if (data.success) {
                    window.location.href = data.redirect;
                } else {
                    document.getElementById('errorMessage').textContent = data.error || 'Login failed';
                }
            } catch (error) {
                console.error('Login error:', error);
                document.getElementById('errorMessage').textContent = 'An error occurred during login';
            }
        });
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt2/views/login.html"}

