{"content":"// Middleware for authentication
const jwt = require('jsonwebtoken');

const authenticate = (req, res, next) => {
  // Check for JWT token in cookies or Authorization header
  let token = req.cookies.token || req.headers.authorization?.split(' ')[1];

  if (!token) {
    return res.status(401).json({ error: 'Access denied. No token provided.' });
  }

  try {
    // Verify token
    const decoded = jwt.verify(token, process.env.JWT_SECRET || 'your-jwt-secret-here');
    req.user = decoded;
    next();
  } catch (error) {
    res.status(400).json({ error: 'Invalid token.' });
  }
};

module.exports = { authenticate };","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt2/middleware/auth.js"}

