        <div class="main-content">
            <h1 class="section-title">Dashboard</h1>

            <div class="welcome-section">
                <p>Welcome to BlueMind v5! This is your personal dashboard where you can manage your account and view important notifications.</p>
            </div>

            <h2 class="section-title">Recent Messages</h2>
            <div id="messagesContainer" class="message-list">
                <!-- Messages will be loaded here dynamically -->
            </div>

            <div style="margin-top: 20px;">
                <a href="/messages" class="btn">View All Messages</a>
                <a href="#" class="btn btn-secondary">Compose Message</a>
            </div>
        </div>
    </div>

    <script>
        // Load messages when the page loads
        document.addEventListener('DOMContentLoaded', function() {
            loadMessages();
        });

        async function loadMessages() {
            try {
                const response = await fetch('/api/messages');
                const messages = await response.json();

                const container = document.getElementById('messagesContainer');
                container.innerHTML = '';

                if (messages.length === 0) {
                    container.innerHTML = '<p>No messages found.</p>';
                    return;
                }

                messages.forEach(message => {
                    const messageElement = document.createElement('div');
                    messageElement.className = 'message-item';
                    messageElement.innerHTML = `
                        <div class="message-from">From: ${message.from}</div>
                        <div class="message-subject">${message.subject}</div>
                        <div class="message-content">${message.content}</div>
                        <div class="message-timestamp">${new Date(message.timestamp).toLocaleString()}</div>
                    `;
                    container.appendChild(messageElement);
                });
            } catch (error) {
                console.error('Error loading messages:', error);
                document.getElementById('messagesContainer').innerHTML = '<p>Error loading messages.</p>';
            }
        }
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt2/views/dashboard.html"}

