<body>
    <!-- Header -->
    <header class="header">
        <div class="logo">
            <div class="hexagon"></div>
            <div class="logo-text">BlueMind</div>
        </div>
        <div class="user-info">Welcome, Student</div>
    </header>

    <!-- Main Content -->
    <div class="container">
        <!-- Sidebar -->
        <div class="sidebar">
            <h3>Navigation</h3>
            <a href="/dashboard" class="nav-link">Dashboard</a>
            <a href="/messages" class="nav-link">Messages</a>
            <a href="#" class="nav-link">Settings</a>
            <a href="#" class="nav-link">Profile</a>
            <a href="/logout" class="nav-link">Logout</a>
        </div>

        <!-- Main Content Area -->
        <div class="main-content">
            <h1 class="section-title">Messages</h1>

            <div id="messagesContainer" class="message-list">
                <!-- Messages will be loaded here dynamically -->
            </div>

            <div class="compose-form">
                <h2 class="section-title">Compose New Message</h2>
                <form id="composeForm">
                    <div class="form-group">
                        <label for="to">To:</label>
                        <input type="text" id="to" name="to" required>
                    </div>
                    <div class="form-group">
                        <label for="subject">Subject:</label>
                        <input type="text" id="subject" name="subject" required>
                    </div>
                    <div class="form-group">
                        <label for="content">Message:</label>
                        <textarea id="content" name="content" required></textarea>
                    </div>
                    <button type="submit" class="btn">Send Message</button>
                </form>
            </div>
        </div>
    </div>

    <script>
        // Load messages when the page loads
        document.addEventListener('DOMContentLoaded', function() {
            loadMessages();
        });

        async function loadMessages() {
            try {
                const response = await fetch('/api/messages');
                const messages = await response.json();

                const container = document.getElementById('messagesContainer');
                container.innerHTML = '';

                if (messages.length === 0) {
                    container.innerHTML = '<p>No messages found.</p>';
                    return;
                }

                messages.forEach(message => {
                    const messageElement = document.createElement('div');
                    messageElement.className = 'message-item';
                    messageElement.innerHTML = `
                        <div class="message-from">From: ${message.from}</div>
                        <div class="message-subject">${message.subject}</div>
                        <div class="message-content">${message.content}</div>
                        <div class="message-timestamp">${new Date(message.timestamp).toLocaleString()}</div>
                    `;
                    container.appendChild(messageElement);
                });
            } catch (error) {
                console.error('Error loading messages:', error);
                document.getElementById('messagesContainer').innerHTML = '<p>Error loading messages.</p>';
            }
        }

        // Handle compose form submission
        document.getElementById('composeForm').addEventListener('submit', async function(e) {
            e.preventDefault();

            const formData = new FormData(this);
            const messageData = {
                to: formData.get('to'),
                subject: formData.get('subject'),
                content: formData.get('content')
            };

            // In a real application, you would send this data to your backend
            alert(`Message would be sent to: ${messageData.to}\nSubject: ${messageData.subject}\nContent: ${messageData.content}`);

            // Reset form
            this.reset();
        });
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt2/views/messages.html"}

