    transition: background-color 0.3s;
}

.connect-button:hover {
    background-color: #1565c0;
}

/* Error message */
.error-message {
    color: #d32f2f;
    margin-top: 10px;
    text-align: center;
}","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt3/blue-mind-v5/public/styles.css"}

