.dashboard-container {
    padding: 20px;
}

.dashboard-header {
    margin-bottom: 30px;
}

.dashboard-header h1 {
    color: white;
    text-align: center;
}

/* Content area */
.content-area {
    display: grid;
    grid-template-columns: 1fr 1fr;
