    margin-bottom: 15px;
}

.message-item {
    padding: 10px;
    border-bottom: 1px solid #eee;
}

.message-item:last-child {
    border-bottom: none;
}

.message-from {
    font-weight: bold;
    color: #1e88e5;
}

.message-content {
    margin-top: 5px;
}

/* Form elements */
.form-group {
    margin-bottom: 15px;
}

.form-group label {
    display: block;
