    grid-template-columns: 1fr 1fr;
    gap: 30px;
}

.messages-section, .send-message-section {
    background-color: white;
    border-radius: 8px;
    padding: 20px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.messages-section h2, .send-message-section h2 {
