document.addEventListener('DOMContentLoaded', function() {
    const loginForm = document.getElementById('loginForm');

    if (loginForm) {
        loginForm.addEventListener('submit', async function(e) {
            e.preventDefault();

            const formData = new FormData(loginForm);
            const login = formData.get('login');
            const password = formData.get('password');
            const computerType = formData.get('computerType');

            try {
                const response = await fetch('/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        login: login,
                        password: password,
                        computerType: computerType
                    })
                });

                const result = await response.json();

                if (result.success) {
                    window.location.href = result.redirect;
                } else {
                    alert(result.error || 'Login failed');
                }
            } catch (error) {
                console.error('Login error:', error);
                alert('An error occurred during login');
            }
        });
    }
});","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt3/blue-mind-v5/public/script.js"}

{"content":"// Dashboard functionality
document.addEventListener('DOMContentLoaded', function() {
    // Get user login from session
    fetch('/dashboard')
        .then(response => {
            if (response.redirected) {
                window.location.href = '/';
                return;
            }
            return response.text();
        })
        .then(html => {
            // Update user login display
            const userLoginElement = document.getElementById('user-login');
            if (userLoginElement) {
                // In a real app, we'd get this from the server-side session
                // For now, we'll simulate it
                userLoginElement.textContent = 'User';
            }

            // Load messages
            loadMessages();
        })
        .catch(error => {
            console.error('Error:', error);
            window.location.href = '/';
        });

    // Send message form handling
    const sendMessageForm = document.getElementById('sendMessageForm');
    if (sendMessageForm) {
        sendMessageForm.addEventListener('submit', async function(e) {
            e.preventDefault();

            const formData = new FormData(sendMessageForm);
            const recipient = formData.get('recipient');
            const content = formData.get('content');

            try {
                const response = await fetch('/send-message', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        to: recipient,
                        content: content
                    })
                });

                const result = await response.json();

                if (result.success) {
                    alert('Message sent successfully!');
                    sendMessageForm.reset();
                    loadMessages(); // Refresh messages
                } else {
                    alert(result.error || 'Failed to send message');
                }
            } catch (error) {
                console.error('Send message error:', error);
                alert('An error occurred while sending the message');
            }
        });
    }

    // Refresh messages button
    const refreshButton = document.getElementById('refresh-messages');
    if (refreshButton) {
        refreshButton.addEventListener('click', loadMessages);
    }
});

// Load and display messages
function loadMessages() {
    fetch('/messages')
        .then(response => response.json())
        .then(messages => {
            const messagesList = document.getElementById('messages-list');
            if (messagesList) {
                if (messages.length === 0) {
                    messagesList.innerHTML = '<p>No messages received</p>';
                    return;
                }

                messagesList.innerHTML = messages.map(message => `
                    <div class="message-item">
                        <span class="message-from">From: ${message.from}</span>
                        <div class="message-content">${message.content}</div>
                        <small>${new Date(message.timestamp).toLocaleString()}</small>
                    </div>
                `).join('');
            }
        })
        .catch(error => {
            console.error('Error loading messages:', error);
            const messagesList = document.getElementById('messages-list');
            if (messagesList) {
                messagesList.innerHTML = '<p>Error loading messages</p>';
            }
        });
}","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt3/blue-mind-v5/public/dashboard-script.js"}

