.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    font-weight: 500;
    color: #555;
}

.form-group input {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 16px;
}

/* Radio buttons */
.radio-group {
    margin-bottom: 25px;
}

.radio-option {
    margin-bottom: 10px;
}

.radio-option input[type="radio"] {
    margin-right: 8px;
}

