        }

        body {
            background-color: #0a2e5c; /* Dark blue background */
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        /* Header styling */
        .header {
            background-color: #001f4d; /* Dark navy-blue background */
            height: 60px;
            display: flex;
            justify-content: center;
            align-items: center;
            box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .logo-icon {
            width: 30px;
            height: 30px;
            background-color: #ffffff;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        /* Main content */
        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-grow: 1;
            padding: 20px;
        }

        .login-panel {
            background-color: white;
            width: 100%;
            max-width: 400px;
            padding: 40px 30px;
            border-radius: 10px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
            border: 1px solid #e0e0e0;
        }

        .panel-title {
            text-align: center;
            color: #333;
            margin-bottom: 30px;
            font-size: 24px;
            font-weight: 600;
        }

        .form-group {
            margin-bottom: 20px;
        }

        label {
            display: block;
            margin-bottom: 8px;
            color: #555;
            font-weight: 500;
        }

        input[type="text"],
        input[type="password"] {
            width: 100%;
            padding: 12px 15px;
            border: 1px solid #ddd;
            border-radius: 5px;
            font-size: 16px;
            transition: border-color 0.3s;
        }

        input[type="text"]:focus,
        input[type="password"]:focus {
            outline: none;
            border-color: #0a2e5c;
        }

        .radio-group {
            margin: 25px 0;
        }

        .radio-option {
            display: flex;
            align-items: center;
            margin-bottom: 15px;
        }

        .radio-option input {
            margin-right: 10px;
        }

        .connect-btn {
            width: 100%;
            padding: 14px;
            background-color: #0a2e5c; /* Bright blue color */
            color: white;
            border: none;
            border-radius: 5px;
            font-size: 18px;
            font-weight: bold;
            cursor: pointer;
            transition: background-color 0.3s;
