            font-weight: bold;
            color: #0a2e5c;
        }

        .timestamp {
            color: #888;
            font-size: 14px;
        }

        .message-content {
            color: #555;
            line-height: 1.5;
        }

        .send-message-form {
            background-color: white;
            border-radius: 10px;
            padding: 20px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
        }

        .form-group {
            margin-bottom: 15px;
        }

        .form-group label {
            display: block;
            margin-bottom: 5px;
            color: #555;
        }

        .form-group input,
        .form-group textarea {
            width: 100%;
            padding: 10px;
            border: 1px solid #ddd;
            border-radius: 5px;
        }

