    <!-- Header -->
    <div class="header">
        <div class="logo">
            <div class="logo-icon"></div>
            <div class="logo-text">BlueMind</div>
        </div>
    </div>

    <!-- Login Panel -->
    <div class="container">
        <div class="login-panel">
            <h2 class="panel-title">Identification</h2>

            <form id="loginForm">
                <div class="form-group">
                    <label for="login">Login</label>
                    <input type="text" id="login" name="login" required>
                </div>

                <div class="form-group">
                    <label for="password">Password</label>
                    <input type="password" id="password" name="password" required>
                </div>

                <div class="radio-group">
                    <div class="radio-option">
                        <input type="radio" id="private" name="computerType" value="private" checked>
                        <label for="private">Private computer</label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="public" name="computerType" value="public">
                        <label for="public">Public computer</label>
                    </div>
                </div>

                <button type="submit" class="connect-btn">Connect</button>
            </form>

            <div id="errorMessage" class="error-message"></div>
        </div>
    </div>

    <script>
        document.getElementById('loginForm').addEventListener('submit', async (e) => {
            e.preventDefault();

            const login = document.getElementById('login').value;
            const password = document.getElementById('password').value;
            const computerType = document.querySelector('input[name="computerType"]:checked').value;

            const errorMessage = document.getElementById('errorMessage');
            errorMessage.textContent = '';

            try {
                const response = await fetch('/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ login, password, computerType })
                });

                const data = await response.json();

                if (response.ok) {
                    window.location.href = data.redirect;
                } else {
                    errorMessage.textContent = data.error || 'Login failed';
                }
            } catch (error) {
                errorMessage.textContent = 'Network error. Please try again.';
                console.error('Login error:', error);
            }
        });
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-30B/prompt4/blue-mind-app/public/login.html"}

{"content":"<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BlueMind v5 - Dashboard</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        body {
            background-color: #0a2e5c; /* Dark blue background */
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        /* Header styling */
        .header {
            background-color: #001f4d; /* Dark navy-blue background */
            height: 60px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 0 20px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .logo-icon {
            width: 30px;
            height: 30px;
            background-color: #ffffff;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        .user-info {
            color: white;
            display: flex;
            align-items: center;
            gap: 15px;
        }

        .logout-btn {
            background-color: #e74c3c;
            color: white;
            border: none;
            padding: 8px 15px;
            border-radius: 4px;
            cursor: pointer;
            font-weight: 500;
            transition: background-color 0.3s;
        }

        .logout-btn:hover {
            background-color: #c0392b;
        }

        /* Main content */
        .container {
            display: flex;
            flex-grow: 1;
            padding: 20px;
            gap: 20px;
        }

        .sidebar {
            width: 250px;
            background-color: white;
            border-radius: 10px;
            padding: 20px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
        }

        .sidebar h3 {
            margin-bottom: 15px;
            color: #333;
            border-bottom: 1px solid #eee;
            padding-bottom: 10px;
        }

        .sidebar-menu {
            list-style: none;
        }

        .sidebar-menu li {
            margin-bottom: 10px;
        }

        .sidebar-menu a {
            text-decoration: none;
            color: #555;
            display: block;
            padding: 8px 12px;
            border-radius: 5px;
            transition: background-color 0.3s;
        }

        .sidebar-menu a:hover {
            background-color: #f0f0f0;
        }

        .main-content {
            flex-grow: 1;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }

        .messages-section {
            background-color: white;
            border-radius: 10px;
            padding: 20px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
            flex-grow: 1;
        }

        .section-title {
            margin-bottom: 20px;
            color: #333;
            font-size: 20px;
        }

        .message-list {
            max-height: 400px;
            overflow-y: auto;
        }

        .message-item {
            padding: 15px;
            border-bottom: 1px solid #eee;
        }

        .message-item:last-child {
            border-bottom: none;
        }

        .message-header {
            display: flex;
            justify-content: space-between;
            margin-bottom: 10px;
        }

        .sender {
            font-weight: bold;
            color: #0a2e5c;
        }

        .timestamp {
            color: #888;
            font-size: 14px;
        }

