            margin-bottom: 20px;
        }

        label {
            display: block;
            margin-bottom: 8px;
            color: #555;
            font-weight: 500;
        }

        input[type="text"],
        input[type="password"] {
            width: 100%;
            padding: 12px 15px;
            border: 1px solid #ddd;
            border-radius: 5px;
            font-size: 16px;
            transition: border-color 0.3s;
        }

        input[type="text"]:focus,
        input[type="password"]:focus {
            outline: none;
            border-color: #0a2e5c;
        }

        .radio-group {
            margin: 25px 0;
        }

        .radio-option {
            display: flex;
            align-items: center;
            margin-bottom: 15px;
        }

        .radio-option input {
            margin-right: 10px;
        }

        .connect-btn {
            width: 100%;
            padding: 14px;
            background-color: #0a2e5c; /* Bright blue color */
            color: white;
            border: none;
            border-radius: 5px;
