                document.getElementById('username').textContent = username;
                loadMessages();
            } else {
                window.location.href = '/';
            }
        });

        // Load messages
        function loadMessages() {
            fetch('/messages')
                .then(response => response.json())
                .then(data => {
                    const container = document.getElementById('messagesContainer');
                    if (data.length === 0) {
                        container.innerHTML = '<p>No messages yet.</p>';
                        return;
                    }

                    container.innerHTML = data.map(message => `
                        <div class="message-item">
                            <div class="message-header">
                                <span class="sender">${message.sender_name}</span>
                                <span class="timestamp">${new Date(message.created_at).toLocaleString()}</span>
                            </div>
                            <div class="message-content">${message.content}</div>
                        </div>
                    `).join('');
                })
                .catch(error => {
                    console.error('Error loading messages:', error);
                    document.getElementById('messagesContainer').innerHTML = '<p>Error loading messages.</p>';
                });
        }

        // Load send message form
        function loadSendMessage() {
            document.getElementById('sendMessageSection').style.display = 'block';
            document.querySelector('.messages-section').style.display = 'none';
        }

        // Handle sending messages
        document.getElementById('sendMessageForm').addEventListener('submit', async (e) => {
            e.preventDefault();

            const recipient = document.getElementById('recipient').value;
            const content = document.getElementById('messageContent').value;
            const errorMessage = document.getElementById('sendErrorMessage');

            errorMessage.textContent = '';

            try {
                const response = await fetch('/send-message', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ recipient, content })
                });

                const data = await response.json();

                if (response.ok) {
                    document.getElementById('recipient').value = '';
                    document.getElementById('messageContent').value = '';
                    errorMessage.textContent = 'Message sent successfully!';
                    setTimeout(() => {
                        document.getElementById('sendErrorMessage').textContent = '';
                    }, 3000);
                } else {
                    errorMessage.textContent = data.error || 'Failed to send message';
                }
            } catch (error) {
                errorMessage.textContent = 'Network error. Please try again.';
                console.error('Send message error:', error);
            }
        });

        // Logout function
        async function logout() {
            try {
                await fetch('/logout', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                sessionStorage.clear();
                window.location.href = '/';
            } catch (error) {
                console.error('Logout error:', error);
                window.location.href = '/';
            }
        }
    </script>
</body>
