  // Create new user
  const newUser = {
    id: users.length + 1,
    login: login,
    password: hashedPassword
  };

  users.push(newUser);

  // Redirect to login
  res.redirect('/login');
});

// Dashboard route
app.get('/dashboard', (req, res) => {
  if (!req.session.user) {
    return res.redirect('/login');
  }

  // Get messages for this user
  const userMessages = messages.filter(m => m.to === req.session.user.login);

  res.render('dashboard', { 
    user: req.session.user,
    messages: userMessages
  });
});

// Send message page
app.get('/send-message', (req, res) => {
  if (!req.session.user) {
    return res.redirect('/login');
  }

  res.render('send-message', { 
    user: req.session.user,
    error: null,
    success: null
  });
});

// Handle sending a message
app.post('/send-message', (req, res) => {
  if (!req.session.user) {
    return res.redirect('/login');
  }

  const { to, subject, content } = req.body;

  // Validate form
  if (!to || !subject || !content) {
    return res.render('send-message', { 
      user: req.session.user,
      error: 'All fields are required',
      success: null
    });
  }

  // Create message
  const newMessage = {
    id: messages.length + 1,
    from: req.session.user.login,
    to: to,
    subject: subject,
    content: content,
    timestamp: new Date()
  };

  messages.push(newMessage);

  res.render('send-message', { 
    user: req.session.user,
    error: null,
    success: 'Message sent successfully!'
  });
});

// Logout
app.get('/logout', (req, res) => {
  req.session.destroy((err) => {
    if (err) {
      return res.redirect('/dashboard');
    }
    res.redirect('/login');
  });
});

// Start server
app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
});","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-Flash/prompt2/app.js"}

{"content":"/* Global Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: #0a2e5c; /* Dark blue background */
    color: #333;
    height: 100vh;
}

/* Header Styles */
.header {
    background-color: #0a2e5c; /* Dark navy-blue background */
    height: 60px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 20px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.2);
}

.logo-container {
    display: flex;
    align-items: center;
    gap: 10px;
}

.hexagon-icon {
    width: 30px;
    height: 30px;
    background-color: #ffffff;
    clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
}

.logo-container h1 {
    color: white;
    font-size: 24px;
    font-weight: bold;
}

.user-info {
    color: white;
    display: flex;
    align-items: center;
    gap: 15px;
}

.logout-link {
    color: #4da6ff;
    text-decoration: none;
    font-size: 14px;
}

.logout-link:hover {
    text-decoration: underline;
}

/* Login Container */
.login-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: calc(100vh - 60px); /* Subtract header height */
}

.login-panel {
    background-color: white;
    border-radius: 8px;
    padding: 30px;
    width: 100%;
    max-width: 400px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    border: 1px solid #e0e0e0;
}

.panel-title {
    text-align: center;
    color: #333;
    margin-bottom: 25px;
    font-size: 24px;
}

.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    font-weight: 600;
    color: #555;
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 16px;
}

.radio-group {
    margin-bottom: 25px;
}

.radio-option {
    margin-bottom: 10px;
    display: flex;
    align-items: center;
    gap: 8px;
}

.radio-option input[type="radio"] {
    margin: 0;
}

.connect-button {
    background-color: #4da6ff; /* Bright blue color */
    color: white;
    border: none;
    border-radius: 4px;
    padding: 12px 20px;
    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    width: 100%;
    transition: background-color 0.3s;
}

.connect-button:hover {
    background-color: #3a8be0;
}

.login-link {
    text-align: center;
    margin-top: 20px;
}

.login-link a {
    color: #4da6ff;
    text-decoration: none;
    font-size: 14px;
}

.login-link a:hover {
    text-decoration: underline;
}

/* Error message styling */
.error-message {
    background-color: #ffebee;
    color: #c62828;
    padding: 12px;
    border-radius: 4px;
    margin-bottom: 20px;
    border: 1px solid #ffcdd2;
}

.success-message {
    background-color: #e8f5e9;
    color: #2e7d32;
    padding: 12px;
    border-radius: 4px;
    margin-bottom: 20px;
    border: 1px solid #c8e6c9;
}

/* Dashboard Styles */
.dashboard-container {
    display: flex;
    min-height: calc(100vh - 60px);
}

.sidebar {
    width: 250px;
    background-color: #f5f5f5;
    padding: 20px;
    border-right: 1px solid #ddd;
}

.sidebar h3 {
    margin-bottom: 15px;
    color: #0a2e5c;
}

.sidebar ul {
    list-style: none;
}

.sidebar li {
    margin-bottom: 10px;
}

.sidebar a {
    text-decoration: none;
    color: #333;
    padding: 8px 12px;
    border-radius: 4px;
    display: block;
    transition: background-color 0.3s;
}

.sidebar a:hover {
    background-color: #e0e0e0;
}

.main-content {
    flex: 1;
    padding: 30px;
}

.main-content h2 {
    color: #0a2e5c;
    margin-bottom: 20px;
}

.messages-section {
    margin-bottom: 30px;
}

.messages-section h3 {
    color: #0a2e5c;
    margin-bottom: 15px;
}

.message-item {
    background-color: #f9f9f9;
    border: 1px solid #eee;
    border-radius: 4px;
    padding: 15px;
    margin-bottom: 10px;
}

.actions {
    margin-top: 30px;
}

.btn {
    padding: 10px 20px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    text-decoration: none;
    display: inline-block;
    font-weight: 600;
}

.btn-primary {
    background-color: #4da6ff;
    color: white;
}

.btn-primary:hover {
    background-color: #3a8be0;
}","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-Flash/prompt2/public/styles.css"}

