            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        .user-info {
            color: white;
            font-size: 16px;
        }

        /* Main content styling */
        .container {
            display: flex;
            flex-grow: 1;
            padding: 20px;
        }

        .sidebar {
            width: 250px;
            background-color: white;
            border-radius: 8px;
            padding: 20px;
            margin-right: 20px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
        }

        .sidebar h3 {
            margin-bottom: 15px;
            color: #333;
        }

        .nav-item {
            padding: 10px 0;
            border-bottom: 1px solid #eee;
            cursor: pointer;
            color: #555;
        }

        .nav-item:hover {
            color: #007bff;
        }

        .messages-area {
            flex-grow: 1;
            background-color: white;
            border-radius: 8px;
            padding: 20px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
            overflow-y: auto;
        }

        .message-list {
            margin-top: 20px;
        }

        .message-item {
            padding: 15px;
            border-bottom: 1px solid #eee;
            cursor: pointer;
        }

        .message-item:hover {
            background-color: #f8f9fa;
        }

        .message-subject {
            font-weight: bold;
            color: #333;
        }

        .message-sender {
            color: #666;
            font-size: 14px;
        }

        .message-preview {
            color: #888;
            margin-top: 5px;
            font-size: 14px;
        }

        .message-details {
            margin-top: 20px;
            padding: 20px;
            border-top: 1px solid #eee;
        }

        .message-header {
            margin-bottom: 15px;
        }

        .message-title {
            font-size: 20px;
            color: #333;
        }

        .message-meta {
            color: #666;
            font-size: 14px;
        }

        .message-content {
            line-height: 1.6;
            color: #444;
        }

        .logout-btn {
            background-color: #dc3545;
            color: white;
            border: none;
            border-radius: 4px;
            padding: 8px 15px;
            font-size: 14px;
            cursor: pointer;
            transition: background-color 0.3s;
        }

        .logout-btn:hover {
            background-color: #c82333;
        }
    </style>
