            max-width: 400px;
            padding: 30px;
            margin-top: 20px;
        }

        .panel-title {
            text-align: center;
            color: #333;
            margin-bottom: 25px;
            font-size: 22px;
        }

        .form-group {
            margin-bottom: 20px;
        }

        label {
            display: block;
            margin-bottom: 8px;
            color: #555;
            font-weight: 500;
        }

        input[type="text"],
        input[type="password"] {
            width: 100%;
            padding: 12px;
            border: 1px solid #ddd;
            border-radius: 4px;
            font-size: 16px;
        }

        .radio-group {
            margin: 20px 0;
        }

        .radio-option {
            margin-bottom: 10px;
        }

        .radio-option input {
            margin-right: 8px;
        }

        .connect-btn {
            background-color: #007bff; /* Bright blue button */
            color: white;
            border: none;
            border-radius: 4px;
            padding: 12px 20px;
            font-size: 16px;
            font-weight: bold;
            width: 100%;
            cursor: pointer;
            transition: background-color 0.3s;
        }

