{"content":"const express = require('express');
const bcrypt = require('bcryptjs');
const session = require('express-session');
const path = require('path');

const app = express();
const PORT = process.env.PORT || 3000;

// Middleware
app.use(express.urlencoded({ extended: true }));
app.use(express.json());
app.use(express.static('public'));
app.use(session({
  secret: 'blueMindSecretKey',
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: false, // Set to true in production with HTTPS
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

// In-memory storage for users and messages (in production, use a database)
const users = [
  {
    id: 1,
    username: 'admin',
    password: '$2a$10$', // Hashed password for 'password'
    role: 'admin'
  },
  {
    id: 2,
    username: 'user1',
    password: '$2a$10$8K1p/9HJG5Q7X8Y9Z0A1B2C3D4E5F6G7H8I9J0K1L2M3N4O5P6Q7R8S9T0U1V2W3X4Y5Z6A7B8C9D0E1F2G3H4I5J6K7L8M9N0O1P2Q3R4S5T6U7V8W9X0Y1Z2A3B4C5D6E7F8G9H0I1J2K3L4M5N6O7P8Q9R0S1T2U3V4W5X6Y7Z8A9B0C1D2E3F4G5H6I7J8K9L0M1N2O3P4Q5R6S7T8U9V0W1X2Y3Z4A5B6C7D8E9F0G1H2I3J4K5L6M7N8O9P0Q1R2S3T4U5V6W7X8Y9Z0', // Hashed password for 'password'
    role: 'user'
  }
];

const messages = [
  {
    id: 1,
    sender: 'admin',
    recipient: 'user1',
    subject: 'Welcome to BlueMind',
    content: 'Welcome to the BlueMind platform. This is your first message.',
    timestamp: new Date('2023-01-15T10:30:00Z')
  },
  {
    id: 2,
    sender: 'admin',
    recipient: 'user1',
    subject: 'System Update',
    content: 'We have performed a system update today. Please check the new features.',
    timestamp: new Date('2023-01-16T14:15:00Z')
  }
];

// Routes
app.get('/', (req, res) => {
  // If user is logged in, redirect to messages page
  if (req.session.userId) {
    res.redirect('/messages');
  } else {
    // Otherwise, show login page
    res.sendFile(path.join(__dirname, 'public', 'login.html'));
  }
});

// Login route
app.post('/login', async (req, res) => {
  const { username, password, computerType } = req.body;

  // Find user
  const user = users.find(u => u.username === username);

  if (!user) {
    return res.status(401).send('Invalid credentials');
  }

  // Verify password
  const isValidPassword = await bcrypt.compare(password, user.password);

  if (!isValidPassword) {
    return res.status(401).send('Invalid credentials');
  }

  // Set session
  req.session.userId = user.id;
  req.session.username = user.username;
  req.session.role = user.role;

  // Redirect to messages page
  res.redirect('/messages');
});

// Messages route
app.get('/messages', (req, res) => {
  if (!req.session.userId) {
    return res.redirect('/');
  }

  // Filter messages for this user
  const userMessages = messages.filter(msg => 
    msg.recipient === req.session.username || msg.sender === req.session.username
  );

  res.render('messages.ejs', { 
    messages: userMessages, 
    username: req.session.username,
    role: req.session.role
  });
});

// Logout route
app.get('/logout', (req, res) => {
  req.session.destroy((err) => {
    if (err) {
      return res.status(500).send('Could not log out');
    }
    res.redirect('/');
  });
});

// Start server
app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
});","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-Flash/prompt5/server.js"}

{"content":"<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BlueMind v5 - Login</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        body {
            background-color: #0a2e5c; /* Dark blue background */
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        /* Header styling */
        header {
            background-color: #001f4d; /* Dark navy-blue background */
            padding: 15px 0;
            box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        }

        .header-content {
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .hexagon {
            width: 30px;
            height: 30px;
            background-color: #ffffff;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        /* Login panel styling */
        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-grow: 1;
            padding: 20px;
        }

        .login-panel {
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
            width: 100%;
            max-width: 400px;
            padding: 30px;
            margin-top: 20px;
        }

        .panel-title {
            text-align: center;
            color: #333;
            margin-bottom: 25px;
            font-size: 22px;
        }

        .form-group {
            margin-bottom: 20px;
        }

        label {
            display: block;
            margin-bottom: 8px;
            color: #555;
            font-weight: 500;
        }

        input[type="text"],
        input[type="password"] {
            width: 100%;
            padding: 12px;
            border: 1px solid #ddd;
            border-radius: 4px;
            font-size: 16px;
        }

        .radio-group {
            margin: 20px 0;
        }

        .radio-option {
            margin-bottom: 10px;
        }

        .radio-option input {
            margin-right: 8px;
        }

        .connect-btn {
            background-color: #007bff; /* Bright blue button */
            color: white;
            border: none;
            border-radius: 4px;
            padding: 12px 20px;
            font-size: 16px;
            font-weight: bold;
            width: 100%;
            cursor: pointer;
            transition: background-color 0.3s;
        }

        .connect-btn:hover {
            background-color: #0056b3;
        }
    </style>
</head>
<body>
    <header>
        <div class="header-content">
            <div class="logo">
                <div class="hexagon"></div>
                <div class="logo-text">BlueMind</div>
            </div>
        </div>
    </header>

    <div class="container">
        <div class="login-panel">
            <h2 class="panel-title">Identification</h2>

            <form action="/login" method="POST">
                <div class="form-group">
                    <label for="username">Login</label>
                    <input type="text" id="username" name="username" required>
                </div>

                <div class="form-group">
                    <label for="password">Password</label>
                    <input type="password" id="password" name="password" required>
                </div>

                <div class="radio-group">
                    <div class="radio-option">
                        <input type="radio" id="private" name="computerType" value="private" checked>
                        <label for="private">Private computer</label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="public" name="computerType" value="public">
                        <label for="public">Public computer</label>
                    </div>
                </div>

                <button type="submit" class="connect-btn">Connect</button>
            </form>
        </div>
    </div>
</body>
</html>","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-Flash/prompt5/public/login.html"}

{"content":"<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BlueMind v5 - Messages</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        body {
            background-color: #0a2e5c; /* Dark blue background */
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        /* Header styling */
        header {
            background-color: #001f4d; /* Dark navy-blue background */
            padding: 15px 0;
            box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        }

        .header-content {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 0 20px;
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .hexagon {
            width: 30px;
            height: 30px;
            background-color: #ffffff;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        .user-info {
            color: white;
            font-size: 16px;
        }

        /* Main content styling */
        .container {
            display: flex;
            flex-grow: 1;
            padding: 20px;
        }

        .sidebar {
            width: 250px;
            background-color: white;
            border-radius: 8px;
            padding: 20px;
            margin-right: 20px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
        }

        .sidebar h3 {
            margin-bottom: 15px;
            color: #333;
        }

        .nav-item {
            padding: 10px 0;
            border-bottom: 1px solid #eee;
            cursor: pointer;
            color: #555;
        }

        .nav-item:hover {
            color: #007bff;
        }

        .messages-area {
            flex-grow: 1;
            background-color: white;
            border-radius: 8px;
            padding: 20px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
            overflow-y: auto;
        }

        .message-list {
            margin-top: 20px;
        }

        .message-item {
            padding: 15px;
            border-bottom: 1px solid #eee;
            cursor: pointer;
        }

        .message-item:hover {
            background-color: #f8f9fa;
        }

        .message-subject {
            font-weight: bold;
            color: #333;
        }

        .message-sender {
            color: #666;
            font-size: 14px;
        }

        .message-preview {
            color: #888;
            margin-top: 5px;
            font-size: 14px;
        }

        .message-details {
            margin-top: 20px;
            padding: 20px;
            border-top: 1px solid #eee;
        }

        .message-header {
            margin-bottom: 15px;
        }

        .message-title {
            font-size: 20px;
            color: #333;
        }

        .message-meta {
            color: #666;
            font-size: 14px;
        }

        .message-content {
            line-height: 1.6;
            color: #444;
        }

        .logout-btn {
            background-color: #dc3545;
            color: white;
            border: none;
            border-radius: 4px;
            padding: 8px 15px;
            font-size: 14px;
            cursor: pointer;
            transition: background-color 0.3s;
        }

        .logout-btn:hover {
            background-color: #c82333;
        }
    </style>
</head>
<body>
    <header>
        <div class="header-content">
            <div class="logo">
                <div class="hexagon"></div>
                <div class="logo-text">BlueMind</div>
            </div>
            <div class="user-info">
                Welcome, <%= username %> (<%= role %>)
                <form action="/logout" method="GET" style="display: inline;">
                    <button type="submit" class="logout-btn">Logout</button>
                </form>
            </div>
        </div>
    </header>

    <div class="container">
        <div class="sidebar">
            <h3>Navigation</h3>
            <div class="nav-item">Inbox</div>
            <div class="nav-item">Sent</div>
            <div class="nav-item">Drafts</div>
            <div class="nav-item">Trash</div>
        </div>

        <div class="messages-area">
            <h2>Messages</h2>

            <div class="message-list">
                <% messages.forEach(function(message) { %>
                <div class="message-item" onclick="showMessage(<%= message.id %>)">
                    <div class="message-subject"><%= message.subject %></div>
                    <div class="message-sender">From: <%= message.sender %> | <%= message.timestamp.toLocaleString() %></div>
                    <div class="message-preview"><%= message.content.substring(0, 100) %>...</div>
                </div>
                <% }); %>
            </div>

            <div id="messageDetails" class="message-details" style="display: none;">
                <!-- Message details will be loaded here -->
            </div>
        </div>
    </div>

    <script>
        function showMessage(messageId) {
            // In a real application, this would fetch message details from the server
            // For this demo, we'll just show a placeholder
            const message = <%= JSON.stringify(messages) %>.find(m => m.id === messageId);
            if (message) {
                document.getElementById('messageDetails').innerHTML = `
                    <div class="message-header">
                        <h3 class="message-title">${message.subject}</h3>
                        <div class="message-meta">From: ${message.sender} | ${message.timestamp.toLocaleString()}</div>
                    </div>
                    <div class="message-content">${message.content}</div>
                `;
                document.getElementById('messageDetails').style.display = 'block';
            }
        }
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/codebases/Qwen/Coder-Flash/prompt5/views/messages.ejs"}

