    <!-- Main content area -->
    <main class="main-content">
        <div class="login-panel">
            <h2 class="title">Identification</h2>

            <form id="loginForm">
                <div class="form-group">
                    <label for="login">Login</label>
                    <input type="text" id="login" name="login" required>
                </div>

