</head>
<body>
    <!-- Header with BlueMind logo -->
    <header class="header">
        <div class="logo-container">
            <div class="logo-icon">B</div>
            <div class="logo-text">BlueMind</div>
        </div>
    </header>

    <!-- Main content area -->
    <main class="main-content">
        <div class="login-panel">
            <h2 class="title">Identification</h2>

            <form id="loginForm">
                <div class="form-group">
                    <label for="login">Login</label>
                    <input type="text" id="login" name="login" required>
                </div>

                <div class="form-group">
                    <label for="password">Password</label>
                    <input type="password" id="password" name="password" required>
                </div>

                <div class="radio-options">
                    <div class="radio-option">
                        <input type="radio" id="private" name="computer-type" value="private" checked>
                        <label for="private">Private computer</label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="public" name="computer-type" value="public">
                        <label for="public">Public computer</label>
                    </div>
                </div>

                <button type="submit" class="connect-button">Connect</button>
            </form>

            <div id="errorMessage" class="error-message"></div>
            <div id="successMessage" class="success-message"></div>
        </div>
    </main>

    <script>
        document.getElementById('loginForm').addEventListener('submit', async function(e) {
            e.preventDefault();

            const login = document.getElementById('login').value;
            const password = document.getElementById('password').value;
            const computerType = document.querySelector('input[name="computer-type"]:checked').value;

            const errorMessage = document.getElementById('errorMessage');
            const successMessage = document.getElementById('successMessage');

            // Hide previous messages
            errorMessage.style.display = 'none';
            successMessage.style.display = 'none';

            try {
                const response = await fetch('/api/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        username: login,
                        password: password
                    })
                });

                const data = await response.json();

                if (response.ok) {
                    // Store token in localStorage or sessionStorage based on computer type
                    if (computerType === 'private') {
                        localStorage.setItem('token', data.token);
                    } else {
                        sessionStorage.setItem('token', data.token);
                    }

                    // Show success message and redirect to dashboard
                    successMessage.textContent = 'Login successful! Redirecting...';
                    successMessage.style.display = 'block';

                    setTimeout(() => {
                        window.location.href = '/dashboard.html';
                    }, 1500);
                } else {
                    errorMessage.textContent = data.error || 'Login failed';
                    errorMessage.style.display = 'block';
                }
            } catch (error) {
                console.error('Login error:', error);
                errorMessage.textContent = 'An error occurred. Please try again.';
                errorMessage.style.display = 'block';
            }
        });
    </script>
</body>
</html>"}

