            <div class="logo-icon">B</div>
            <div class="logo-text">BlueMind</div>
        </div>
        <div class="user-info">
            Welcome, <span id="usernameDisplay"></span>
            <button class="logout-btn" id="logoutBtn">Logout</button>
        </div>
    </header>

    <!-- Main content area -->
    <main class="main-content">
        <!-- Sidebar for composing messages -->
        <aside class="sidebar">
            <h3 class="sidebar-title">Compose Message</h3>
            <form id="composeForm" class="compose-form">
                <div class="form-group">
                    <label for="recipient">To:</label>
                    <select id="recipient" name="recipient" required>
                        <option value="">Select recipient...</option>
                    </select>
                </div>
                <div class="form-group">
                    <label for="messageContent">Message:</label>
                    <textarea id="messageContent" name="messageContent" placeholder="Type your message here..." required></textarea>
                </div>
                <button type="submit" class="send-btn">Send Message</button>
            </form>

            <div id="composeErrorMessage" class="error-message"></div>
            <div id="composeSuccessMessage" class="success-message"></div>
        </aside>

        <!-- Messages area -->
        <section class="messages-area">
            <div class="messages-header">
                <h3 class="messages-title">Received Messages</h3>
                <button class="refresh-btn" id="refreshBtn">Refresh</button>
            </div>
            <div class="messages-list" id="messagesList">
                <!-- Messages will be loaded here dynamically -->
            </div>
        </section>
    </main>

    <script>
        // Get token from localStorage or sessionStorage
        const token = localStorage.getItem('token') || sessionStorage.getItem('token');

        if (!token) {
            window.location.href = '/';
            exit();
        }

        // Display username
        try {
            const payload = JSON.parse(atob(token.split('.')[1]));
            document.getElementById('usernameDisplay').textContent = payload.username;
        } catch (e) {
            console.error('Error parsing token:', e);
        }

        // Fetch users for recipient dropdown
        async function loadRecipients() {
            try {
                const response = await fetch('/api/users', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const users = await response.json();
                    const recipientSelect = document.getElementById('recipient');

                    // Clear existing options except the first one
                    recipientSelect.innerHTML = '<option value="">Select recipient...</option>';

                    users.forEach(user => {
                        const option = document.createElement('option');
                        option.value = user.username;
                        option.textContent = user.username;
                        recipientSelect.appendChild(option);
                    });
                } else {
                    console.error('Failed to load recipients');
                }
            } catch (error) {
                console.error('Error loading recipients:', error);
            }
        }

        // Load messages
        async function loadMessages() {
            try {
                const response = await fetch('/api/messages', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const messages = await response.json();
                    const messagesList = document.getElementById('messagesList');

                    if (messages.length === 0) {
                        messagesList.innerHTML = '<div class="empty-messages">No messages received yet</div>';
                        return;
                    }

                    messagesList.innerHTML = '';

                    messages.forEach(message => {
                        const messageElement = document.createElement('div');
                        messageElement.className = 'message-item';

                        const timestamp = new Date(message.timestamp).toLocaleString();

                        messageElement.innerHTML = `
                            <div class="message-header">
                                <div class="message-sender">From: ${message.sender_name}</div>
                                <div class="message-time">${timestamp}</div>
                            </div>
                            <div class="message-content">${message.content}</div>
                        `;

                        messagesList.appendChild(messageElement);
                    });
                } else {
                    console.error('Failed to load messages');
                }
            } catch (error) {
                console.error('Error loading messages:', error);
            }
        }

        // Send message
        document.getElementById('composeForm').addEventListener('submit', async function(e) {
            e.preventDefault();

            const recipient = document.getElementById('recipient').value;
            const content = document.getElementById('messageContent').value;

            const errorMessage = document.getElementById('composeErrorMessage');
            const successMessage = document.getElementById('composeSuccessMessage');

            // Hide previous messages
            errorMessage.style.display = 'none';
            successMessage.style.display = 'none';

            if (!recipient || !content) {
                errorMessage.textContent = 'Please fill in all fields';
                errorMessage.style.display = 'block';
                return;
            }

            try {
                const response = await fetch('/api/messages', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        receiver_username: recipient,
                        content: content
                    })
                });

                const data = await response.json();

                if (response.ok) {
                    successMessage.textContent = 'Message sent successfully!';
                    successMessage.style.display = 'block';

                    // Reset form
                    document.getElementById('composeForm').reset();

                    // Reload messages after a delay
                    setTimeout(loadMessages, 1000);
                } else {
                    errorMessage.textContent = data.error || 'Failed to send message';
                    errorMessage.style.display = 'block';
                }
            } catch (error) {
                console.error('Error sending message:', error);
                errorMessage.textContent = 'An error occurred. Please try again.';
                errorMessage.style.display = 'block';
            }
        });

        // Logout
        document.getElementById('logoutBtn').addEventListener('click', async function() {
            try {
                await fetch('/api/logout', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });

                // Remove token
                localStorage.removeItem('token');
                sessionStorage.removeItem('token');

                // Redirect to login
                window.location.href = '/';
            } catch (error) {
                console.error('Logout error:', error);
            }
        });

        // Refresh messages
        document.getElementById('refreshBtn').addEventListener('click', loadMessages);

        // Initialize the page
        loadRecipients();
        loadMessages();
    </script>
</body>
</html>"}

