        .logo-icon {
            width: 30px;
            height: 30px;
            background-color: #4dabf7; /* Bright blue */
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
            display: flex;
            align-items: center;
