        email: req.session.userEmail, 
        recipients: otherUsers,
        error: 'Cannot send message to yourself' 
      });
    });
  }

  db.sendMessage(req.session.userId, recipientId, subject, message, (err) => {
    if (err) {
      return db.getAllUsers((err, users) => {
