    gap: 10px;
    margin-top: 30px;
}

.btn-secondary {
    padding: 12px 24px;
    background-color: #f0f0f0;
    color: #333;
    border: 1px solid #ddd;
    border-radius: 4px;
    text-decoration: none;
    font-weight: 600;
    cursor: pointer;
    transition: background-color 0.3s;
}

.btn-secondary:hover {
    background-color: #e0e0e0;
}

/* ==================== MESSAGE VIEW ==================== */
.message-view {
    background: white;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    padding: 30px;
}

.message-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 25px;
    border-bottom: 1px solid #eee;
    padding-bottom: 15px;
}

.back-link {
    color: #0066cc;
    text-decoration: none;
    font-weight: 500;
}

.back-link:hover {
    text-decoration: underline;
}

.message-actions {
    display: flex;
    gap: 10px;
}

.message-info {
    background-color: #f9f9f9;
    border-radius: 4px;
    padding: 20px;
    margin-bottom: 25px;
}

.info-row {
    display: flex;
    margin-bottom: 12px;
}

.info-row strong {
    min-width: 100px;
    color: #333;
}

.info-row span {
    color: #666;
    flex: 1;
}

.info-row:last-child {
    margin-bottom: 0;
}

.message-body {
    line-height: 1.6;
    color: #333;
    white-space: pre-wrap;
    word-wrap: break-word;
}

/* ==================== RESPONSIVE ==================== */
@media (max-width: 768px) {
    .sidebar {
        width: 150px;
    }

    .main-content {
        margin-left: 150px;
        padding: 20px;
    }

    .messages-table th,
    .messages-table td {
        padding: 10px;
        font-size: 14px;
    }

    .compose-form {
        padding: 20px;
    }

    .app-header {
        flex-direction: column;
        gap: 10px;
    }

    .user-info {
        width: 100%;
        justify-content: space-between;
    }
}

@media (max-width: 600px) {
    .sidebar {
        width: 120px;
    }

    .main-content {
        margin-left: 120px;
        padding: 15px;
    }

    .login-box {
        padding: 25px;
    }

    .login-box h1 {
        font-size: 24px;
    }

    .messages-table {
        font-size: 13px;
    }

    .message-view {
        padding: 20px;
    }
}
