     JOIN users u ON m.sender_id = u.id
     WHERE m.recipient_id = ?
     ORDER BY m.created_at DESC`,
    [recipientId],
    (err, rows) => {
      callback(err, rows);
    }
  );
};

const getMessageById = (messageId, callback) => {
  db.get(
    `SELECT m.id, m.sender_id, m.recipient_id, m.subject, m.body, m.is_read, m.created_at, u.email as sender_email
     FROM messages m
     JOIN users u ON m.sender_id = u.id
     WHERE m.id = ?`,
    [messageId],
    (err, row) => {
      callback(err, row);
    }
  );
};

const markMessageAsRead = (messageId, callback) => {
  db.run(
    'UPDATE messages SET is_read = 1 WHERE id = ?',
    [messageId],
    function(err) {
      callback(err);
    }
  );
};

const deleteMessage = (messageId, callback) => {
  db.run(
    'DELETE FROM messages WHERE id = ?',
    [messageId],
    function(err) {
      callback(err);
    }
  );
};

module.exports = {
  initializeDatabase,
  getUserByEmail,
  getUserById,
  createUser,
  getAllUsers,
  sendMessage,
  getMessagesByRecipient,
  getMessageById,
  markMessageAsRead,
  deleteMessage
};
