.back-link {
    display: inline-block;
    margin-top: 20px;
    color: #00acc1;
    text-decoration: none;
}

.back-link:hover {
    text-decoration: underline;
}

/* Responsive design */
@media (max-width: 768px) {
    .dashboard-content {
        padding: 20px;
    }

    .navbar {
        flex-direction: column;
