    margin-bottom: 20px;
}

.message-header h2 {
    color: #1a237e;
    margin-bottom: 10px;
}

.message-details {
    font-size: 14px;
    color: #666;
}

.message-content {
    font-size: 16px;
    line-height: 1.6;
    color: #333;
    white-space: pre-wrap;
}

.back-link {
    display: inline-block;
    margin-top: 20px;
    color: #00acc1;
    text-decoration: none;
}

.back-link:hover {
