    gap: 20px;
}

.navbar a {
    color: white;
    text-decoration: none;
    padding: 8px 16px;
    border-radius: 4px;
    transition: background-color 0.3s;
}

.navbar a:hover {
    background-color: rgba(255,255,255,0.1);
}

.dashboard-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 30px;
}

.dashboard-header {
