{"content":"body {
    margin: 0;
    padding: 0;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: #00155a;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
}

.login-container {
    text-align: center;
    color: white;
}

.logo {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 20px;
}

.logo img {
    width: 250px;
}

.logo span {
    font-size: 14px;
    margin-top: 5px;
}

.login-card {
    background-color: white;
    padding: 40px;
    width: 450px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
    border-top: 5px solid #ff8a8a; /* Reddish bar at top */
    color: #333;
}

.login-card h2 {
    margin-top: 0;
    margin-bottom: 30px;
    font-weight: 600;
}

.form-group {
    margin-bottom: 25px;
    text-align: left;
    border-bottom: 1px solid #ccc;
    padding-bottom: 5px;
    display: flex;
    align-items: center;
}

.form-group i {
    margin-right: 15px;
    color: #666;
    width: 20px;
}

.form-group label {
    font-weight: bold;
    margin-right: 20px;
    min-width: 80px;
}

.form-group input[type="text"],
.form-group input[type="password"],
.form-group input[type="email"] {
    border: none;
    outline: none;
    flex-grow: 1;
    font-size: 16px;
    color: #333;
}

.form-group .eye-icon {
    cursor: pointer;
}

.radio-group {
    margin-top: 30px;
    text-align: left;
    display: flex;
    flex-direction: column;
    gap: 10px;
    padding-left: 100px;
}

.radio-option {
    display: flex;
    align-items: center;
    font-size: 14px;
    color: #666;
}

.radio-option input {
    margin-right: 10px;
}

.btn-connect {
    background-color: #0091ff;
    color: white;
    border: none;
    padding: 12px 0;
    width: 60%;
    font-size: 18px;
    font-weight: bold;
    border-radius: 4px;
    cursor: pointer;
    margin-top: 40px;
}

.btn-connect:hover {
    background-color: #007bdd;
}

.dashboard-container {
    background-color: white;
    padding: 20px;
    width: 80%;
    max-width: 800px;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0,0,0,0.2);
}

.message {
    border-bottom: 1px solid #eee;
    padding: 10px 0;
}

.message-sender {
    font-weight: bold;
}

.message-time {
    font-size: 12px;
    color: #888;
}
